import { atom } from "recoil";

export interface IUser {
    id: string;
    pwd: string;
    name: string;
}

export const user = atom<IUser>({
    key: "user",
    default: {
        id: "admin",
        pwd: "admin",
        name: "관리자"
    }
});

export const login = atom({
    key: 'loginId', // unique ID(다른 atom/selectors 와 구별하기 위함)
    get: ({get}) => {
        const text = get(textState);

        return text.length;
    },
});
